  FULLTEXT KEY `Title` (`Title`,`Abstract`),
  CONSTRAINT `PostInfo_ibfk_1` FOREIGN KEY (`PostID`) REFERENCES `Post` (`ID`)
) ENGINE=InnoDB AUTO_INCREMENT=630 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;
