CREATE DEFINER=`root`@`%` PROCEDURE `Generate_Tag`(in IPostID int)
begin
		Declare CateID int;
        Declare RandomID int;
        Declare Tag1ID int;
        Declare Tag2ID int;
        Set CateID = (Select P.CategoryID from Post P where P.ID = IPostID limit 1);
        Set RandomID = FLOOR(RAND()*4);
        Set Tag1ID = case 
			when CateID = 12 and RandomID = 0 then 6
            when CateID = 12 and RandomID = 1 then 28
            when CateID = 12 and RandomID = 2 then 29
            when CateID = 12 and RandomID = 3 then 30
            when CateID = 13 and RandomID = 0 then 6
            when CateID = 13 and RandomID = 1 then 31
            when CateID = 13 and RandomID = 2 then 32
            when CateID = 13 and RandomID = 3 then 9
            when CateID = 15 then 6
            when CateID = 16 then 20
            when CateID = 17 and RandomID = 1 then 6
            when CateID = 17 and RandomID = 2 then 33
            when CateID = 17 and RandomID = 3 then 32
            when CateID = 18 and RandomID = 1 then 6
            when CateID = 18 and RandomID = 2 then 33
            when CateID = 18 and RandomID = 3 then 32
            when CateID = 27 then 6
            when CateID = 28 and RandomID = 0 then 33
            when CateID = 28 and RandomID = 1 then 34
            when CateID = 28 and RandomID = 2 then 35
            when CateID = 28 and RandomID = 3 then 36
            when CateID = 29 and RandomID = 0 then 33
            when CateID = 29 and RandomID = 1 then 35
            when CateID = 29 and RandomID = 2 then 27
            when CateID = 30 and RandomID = 0 then 34
            when CateID = 30 and RandomID = 1 then 35
            when CateID = 30 and RandomID = 2 then 37
            when CateID = 31 and RandomID = 0 then 32
            when CateID = 31 and RandomID = 1 then 33
            when CateID = 31 and RandomID = 2 then 38
            when CateID = 33 and RandomID = 0 then 32
            when CateID = 33 and RandomID = 1 then 33
            when CateID = 33 and RandomID = 2 then 38
            when CateID = 34 and RandomID = 0 then 32
            when CateID = 34 and RandomID = 1 then 33
            when CateID = 34 and RandomID = 2 then 38
            when CateID = 39 then 6
		end;
        Set Tag2ID = case 
			when CateID = 12 and RandomID = 0 then 6
            when CateID = 12 and RandomID = 1 then 28
            when CateID = 12 and RandomID = 2 then 29
            when CateID = 12 and RandomID = 3 then 30
            when CateID = 13 and RandomID = 0 then 6
            when CateID = 13 and RandomID = 1 then 31
            when CateID = 13 and RandomID = 2 then 32
            when CateID = 13 and RandomID = 3 then 9
            when CateID = 15 then 6
            when CateID = 16 then 20
            when CateID = 17 and RandomID = 1 then 6
            when CateID = 17 and RandomID = 2 then 33
            when CateID = 17 and RandomID = 3 then 32
            when CateID = 18 and RandomID = 1 then 6
            when CateID = 18 and RandomID = 2 then 33
            when CateID = 18 and RandomID = 3 then 32
            when CateID = 27 then 6
            when CateID = 28 and RandomID = 0 then 33
            when CateID = 28 and RandomID = 1 then 34
            when CateID = 28 and RandomID = 2 then 35
            when CateID = 28 and RandomID = 3 then 36
            when CateID = 29 and RandomID = 0 then 33
            when CateID = 29 and RandomID = 1 then 35
            when CateID = 29 and RandomID = 2 then 27
            when CateID = 30 and RandomID = 0 then 34
            when CateID = 30 and RandomID = 1 then 35
            when CateID = 30 and RandomID = 2 then 37
            when CateID = 31 and RandomID = 0 then 32
            when CateID = 31 and RandomID = 1 then 33
            when CateID = 31 and RandomID = 2 then 38
            when CateID = 33 and RandomID = 0 then 32
            when CateID = 33 and RandomID = 1 then 33
            when CateID = 33 and RandomID = 2 then 38
            when CateID = 34 and RandomID = 0 then 32
            when CateID = 34 and RandomID = 1 then 33
            when CateID = 34 and RandomID = 2 then 38
            when CateID = 39 then 6
		end;
        if Tag1ID is not null then
			insert into PostTag(PostID, TagID) values (IPostID, Tag1ID);
		end if;
        if Tag2ID is not null and Tag1ID <> Tag2ID then
			insert into PostTag(PostID, TagID) values (IPostID, Tag2ID);
		end if;
	end ;;
DELIMITER ;
