CREATE DEFINER=`root`@`%` PROCEDURE `GTR_Detail_Account_By_ID`(in IID int)
begin
		select A.ID, A.Password, A.DisplayName, A.Email, A.AccountType, AI.FirstName, AI.LastName, AI.DOB,
			case 
				when SI.PremiumStatus = "inactive" then "inactive"
                when  SI.PremiumStatus = "Active" and SI.ExpiredDate >= cast(curdate() as datetime) then "Active"
                when  SI.PremiumStatus = "Active" and SI.ExpiredDate < cast(curdate() as datetime) then "inactive"
            end as PremiumStatus
        from Account A left join AccountInfo AI on A.ID = AI.AccountID
        left join SubscriberInfo SI on A.ID = SI.AccountID
        where A.ID = IID;
	end ;;
