/*!50003 SET @saved_sql_mode       = @@sql_mode */ ;
/*!50003 SET sql_mode              = 'ONLY_FULL_GROUP_BY,STRICT_TRANS_TABLES,NO_ZERO_IN_DATE,NO_ZERO_DATE,ERROR_FOR_DIVISION_BY_ZERO,NO_ENGINE_SUBSTITUTION' */ ;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `GTR_Refused_PostDetail_By_ID`(in IID int)
begin
    with CommentCount as (
			select P2.ID as PostID, count(CM.ID) as Count
            from Post P2 left join Comment CM on P2.ID = CM.PostID
            group by P2.ID
			)
		select WI.AccountID as WriterID, WI.Alias as Writer, C.ID as CategoryID, C.Name as CategoryName, P.ID as PostID, PI.Premium, PI.PublishDate, PI.Title, PI.Abstract, PD.Content, PI.ViewCount, CC.Count as TotalComment, PR.Note, PR.ReportDate, PR.AccountID as EditorID, A2.DisplayName as EditorName,
			case 
				when P.Status = 'Draft' then 'Draft'
                when P.Status = 'Refused' then 'Refused'
                when P.Status = 'Publish' and PI.PublishDate <= current_timestamp() then 'Publish'
                when P.Status = 'Publish' and PI.PublishDate > current_timestamp() then 'Unpublish'
            end as Status
        from PostInfo PI join Post P on PI.PostID = P.ID 
        join WriterInfo WI on P.AccountID = WI.AccountID
        join Category C on C.ID = P.CategoryID
        join PostDetail PD on P.ID = PD.PostID
        left join CommentCount CC on P.ID = CC.PostID
        left join PostReport PR on PR.PostID = IID
        left join Account A2 on PR.AccountID = A2.ID
        where P.ID = IID;
	end ;;
DELIMITER ;
