CREATE DEFINER=`root`@`%` PROCEDURE `GTR_Search_PostInfo_Total`(in IKeyword nvarchar(300))
begin
		select Count(*) as Total
		from Post P
			left join PostInfo PI on P.ID = PI.PostID
			left join PostDetail PD on P.ID = PD.PostID
			left join Category C on P.CategoryID = C.ID
		where P.Status = "Publish" and PI.PublishDate < current_timestamp() and
			(match(PI.Title, PI.Abstract) against(IKeyword)
			or match(PD.Content) against(IKeyword));
	end ;;
DELIMITER ;
