CREATE DEFINER=`root`@`%` PROCEDURE `GTT_CategoryManageDetail_By_AccountID`(in IAccountID int)
begin
		with DraftPost as (
			select * from Post P where P.Status = "Draft"
		)
		select C.ID, C.Name, C.ParentCategoryID, C2.Name as ParentName, Count(DP.ID) as NumDraft
        from CategoryManageDetail CM join Category C on CM.CategoryID = C.ID
        right join Category C2 on C.ParentCategoryID = C2.ID
        left join DraftPost DP on DP.CategoryID = C.ID
        where CM.AccountID = IAccountID
        group by (C.ID);
    end ;;
DELIMITER ;
