CREATE DEFINER=`root`@`%` PROCEDURE `GTT_Draft_Post_By_Category`(in ICategoryID int)
begin
		select WI.Alias as Writer, WI.AccountID as WriterID, C.Name as CategoryName, C.ID as CategoryID, P.ID as PostID, PI.Premium, PI.Title, PI.Abstract, PI.PublishDate, PI.ViewCount, P.Status
        from PostInfo PI join Post P on PI.PostID = P.ID 
        join WriterInfo WI on P.AccountID = WI.AccountID
        join Category C on C.ID = P.CategoryID
        where P.CategoryID = ICategoryID and P.Status = "Draft";
    end ;;
