DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `GTT_PostDetail_By_Writer`(in IAccountID int)
begin
		select WI.Alias as Writer, WI.AccountID as WriterID, C.Name as CategoryName, C.ID as CategoryID, P.ID as PostID, PI.Premium, PI.Title, PI.Abstract, PI.PublishDate, PI.ViewCount,
			case 
				when P.Status = 'Draft' then 'Draft'
                when P.Status = 'Refused' then 'Refused'
                when P.Status = 'Publish' and PI.PublishDate <= current_timestamp() then 'Publish'
                when P.Status = 'Publish' and PI.PublishDate > current_timestamp() then 'Unpublish'
            end as Status
        from PostInfo PI join Post P on PI.PostID = P.ID 
