CREATE DEFINER=`root`@`%` PROCEDURE `GTT_PostInfo_Publish_Pagging_By_CategoryID`(in ICategoryID int, in IPage int)
begin
		declare startIndex int;
        set startIndex = IPage*5;
        with CommentCount as (
			select P2.ID as PostID, count(CM.ID) as Count
            from Post P2 left join Comment CM on P2.ID = CM.PostID
            group by P2.ID
			)
		select C.Name as CategoryName, P.ID as PostID, PI.PublishDate, PI.Title, PI.Abstract, PI.Premium, PI.ViewCount, CC.Count as TotalComment
        from PostInfo PI join Post P on PI.PostID = P.ID 
        join WriterInfo WI on P.AccountID = WI.AccountID
        join Category C on C.ID = P.CategoryID
        left join Category C2 on C2.ID = C.ParentCategoryID
		left join CommentCount CC on P.ID = CC.PostID 
        where P.status = "Publish" and (C.ID = ICategoryID or C2.ID = ICategoryID) and PI.PublishDate < current_timestamp()
        order by PI.Premium desc, PI.PublishDate desc
        limit startIndex, 5;
	end ;;
