CREATE DEFINER=`root`@`%` PROCEDURE `GTT_Search_PostInfo_Title`(in IKeyword nvarchar(300), in IPage int)
begin
		declare startIndex int;
        set startIndex = IPage*5;
        with CommentCount as (
			select P2.ID as PostID, count(CM.ID) as Count
            from Post P2 left join Comment CM on P2.ID = CM.PostID
            group by P2.ID
			)
		select P.ID, C.Name as CategoryName, C.ID as CategoryID, PI.PublishDate, PI.Title, PI.Abstract, PI.Premium, PI.ViewCount, CC.Count as TotalComment,
			match(PI.Title, PI.Abstract) against(IKeyword) as PScore
		from Post P
			join PostInfo PI on P.ID = PI.PostID
			left join PostDetail PD on P.ID = PD.PostID
			left join Category C on P.CategoryID = C.ID
            left join CommentCount CC on P.ID = CC.PostID
		where P.Status = "Publish" and PI.PublishDate < current_timestamp() and
			(match(PI.Title, PI.Abstract) against(IKeyword)
			or match(PD.Content) against(IKeyword))
		order by PI.Premium desc, PScore desc
        limit startIndex, 5;
	end ;;
DELIMITER ;
