CREATE DEFINER=`root`@`%` PROCEDURE `GTT_Top10_Cat_PostInfo_With_LastestPost`()
begin
		with CommentCount as (
			select P2.ID as PostID, count(CM.ID) as Count
            from Post P2 left join Comment CM on P2.ID = CM.PostID
            group by P2.ID
			)
		select P.ID, C.Name as CategoryName, C.ID as CategoryID, PI.Title, PI.PublishDate, PI.Abstract, PI.Premium, PI.ViewCount, CC.Count as TotalComment
        from PostInfo PI join Post P on PI.PostID = P.ID 
        join WriterInfo WI on P.AccountID = WI.AccountID
        join Category C on C.ID = P.CategoryID
        join (
			select C.ID as ID
			from PostInfo PI join Post P on PI.PostID = P.ID
			join Category C on C.ID = P.CategoryID
			group by (C.ID)
			order by sum(PI.ViewCount) desc
            limit 10) as C2 on C.ID = C2.ID
		left join CommentCount CC on P.ID = CC.PostID
		where P.Status = "Publish" and PI.PublishDate < current_timestamp()
        and PI.PublishDate >= all(
			select PI1.PublishDate
			from PostInfo PI1 join Post P1
			on P1.ID = PI1.PostID
			where P1.CategoryID = C.ID and  P1.Status = "Publish" and PI1.PublishDate < current_timestamp())
		having P.ID <= all(select P1.ID
			from Post P1 join PostInfo PI1 on P1.ID = PI1.PostID
            where P1.Status = "Publish" and PI1.PublishDate < current_timestamp() and P1.CategoryID = C.ID and PI1.PublishDate >= all(select PI2.PublishDate
				from PostInfo PI2 join Post P2
				on P2.ID = PI2.PostID
				where P2.CategoryID = C.ID and  P2.Status = "Publish" and PI2.PublishDate < current_timestamp()));
	end ;;
DELIMITER ;
