begin
		with CommentCount as (
			select P2.ID as PostID, count(CM.ID) as Count
            from Post P2 left join Comment CM on P2.ID = CM.PostID
            group by P2.ID
			)
		select P.ID, C.ID as CategoryID, C.Name as CategoryName, PI.Title, PI.PublishDate, PI.Abstract, PI.Premium, PI.ViewCount, CC.Count as TotalComment
