CREATE DEFINER=`root`@`%` PROCEDURE `Loop_Generate`()
begin
		DECLARE cursor_ID INT;
		DECLARE done INT DEFAULT FALSE;
		DECLARE cursor_i CURSOR FOR SELECT ID FROM Post;
		DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
		OPEN cursor_i;
		read_loop: LOOP
			FETCH cursor_i INTO cursor_ID;
			IF done THEN
				LEAVE read_loop;
			END IF;
